mapper.select:
select  s.*, c.name as category_name from setmeal s left outer join category c on s.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.status = #{status}


        and d.category_id = #{categoryId}


order by s.create_time desc

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, update_Time = #{updateTime}, update_User = #{updateUser}, status = #{status},
where id = #{id}
